    @RequestMapping(value = "/getProjectDocuments", method = RequestMethod.GET)
    public String getProjectDocuments(HttpServletRequest request,HttpServletResponse response){
    	String projectId = request.getParameter("projectId");
    	List docList = projectDocumentDao.getDocumentsByProject(projectId);
    	String json = null;
    	ObjectMapper mapper=null;	
    	mapper = new ObjectMapper();
    	try {
			json=mapper.writeValueAsString(docList);//把map或者是list转换成
		} catch (JsonProcessingException e) {
			e.printStackTrace();
		}
    	log.info("======docjson:"+json);
    	request.setAttribute("docList", json);
    	return "documents";
    }

    
