    @RequestMapping(value = "/getPddFileList", method = RequestMethod.GET)
    public String getPddFileList(HttpServletRequest request,HttpServletResponse response){
    	File file = new File(PddDownloader.SAVED_DIR);
    	File[] files = file.listFiles(new FilenameFilter() {
    		@Override
			public boolean accept(File dir, String name) {
				 if(name.lastIndexOf('.')>0)
	               {
	                  // get last index for '.' char
	                  int lastIndex = name.lastIndexOf('.');
	                  
	                  // get extension
	                  String str = name.substring(lastIndex);
	                  
	                  // match path name extension
	                  if(str.equalsIgnoreCase(".pdf"))
	                  {
	                     return true;
	                  }
	               }
	               return false;
	            }
			
		});
    	
    	Arrays.sort(files,new Comparator<File>(){

			@Override
			public int compare(File o1, File o2) {
				if(o1.lastModified()==o2.lastModified()){
					return 0;
				}else if (o1.lastModified()>o2.lastModified()){
					return -1;
				}else{
					return 1;
				}
			}
    		
    	});
    	
    	String json = null;
    	ObjectMapper mapper=new ObjectMapper();	
    	ArrayNode arrayNode = mapper.createArrayNode(); 
    	
    	if(files.length>0){
    		for(File f:files){
    			ObjectNode pddinfo = mapper.createObjectNode();
    			pddinfo.put("name", f.getName());
    			
    			SimpleDateFormat dateParser = new SimpleDateFormat("yyyy/MM/dd HH:mm:ss");
    			pddinfo.put("modified", dateParser.format(f.lastModified()));
    			pddinfo.put("path", f.getAbsolutePath());
    			arrayNode.add(pddinfo);
    		}
    			
    	}
    	
    	try {
			json=mapper.writeValueAsString(arrayNode);
		} catch (JsonProcessingException e) {
			e.printStackTrace();
		}
    	
    	log.info("======docjson:"+json);
    	request.setAttribute("docList", json);
    	return "pddFiles";
    
    	
    }
    
    
